const products = [
    {
        name: "Men's T-shirt",
        price: "$15",
        image: "data:image/jpeg;base64,"
    },
    {
        name: "Women's Dress",
        price: "$25",
        image: "https://encrypted-tbn0.gstatic.com/shopping?q=tbn:ANd9GcQqNznJyiBIT7Nia-IJ2CRjiaiBKcAOn6mE68UJUEKuQ67ZycHyr0LKJbXX708vjWEUDjcKHLtuS-oxw6k7snxcsCT3YuprOMLw4PDP-QVdw-vbzbbO64gY"
    },
    {
        name: "Kids' Jacket",
        price: "$30",
        image: "data:image/jpeg;base64,"
    },
    {
        name: "Sneakers",
        price: "$45",
        image: "data:image/jpeg;base64,"
    }
];

function displayProducts(filteredProducts = products) {
    const productGrid = document.getElementById("productGrid");
    productGrid.innerHTML = "";

    filteredProducts.forEach(product => {
        const productDiv = document.createElement("div");
        productDiv.className = "product";
        productDiv.innerHTML = `
            <img src="${product.image}" alt="${product.name}">
            <h3>${product.name}</h3>
            <p>${product.price}</p>
        `;
        productGrid.appendChild(productDiv);
    });
}

displayProducts();

function searchProduct() {
    const query = document.getElementById("searchBox").value.toLowerCase();
    const filtered = products.filter(product => product.name.toLowerCase().includes(query));
    displayProducts(filtered);
}
